import { createStore } from 'redux';

const authReducer = (state = {token: ''}, action, newtoken) => {

    if(action.type === 'login'){
        return {
          token: newtoken  
        };
    }

    if(action.type === 'logout'){
        return {
            token: ''
        };
    }

    return state;
};


const store  = createStore(authReducer);

export default store;
