import { createStore } from 'redux';

const authReducer = (state = {token: ''}, action) => {

    console.log(action);
    if(action.type === 'login'){
        return {
          token: action.token
        };
    }

    if(action.type === 'logout'){
        return {
            token: ''
        };
    }

    return state;
};


const store  = createStore(authReducer);

export default store;
