import React , {Component} from 'react';
import Loan from './Loan';
import classes from './AllLoans.module.css';

class AllLoans extends Component {

    state = {
        loanData: []
    }

    async componentDidMount() {
        const res = await fetch('http://localhost:3000/loan',{
            method: 'GET',
            headers: {
                accept: 'application/json',
            }})
            
        const result = res.json();
        result.then(r => {
            this.setState({loanData: r});
            console.log(r);
        })
              
    }
    render(){

        return (
            <section className={classes.loanHeader}>
            <h1>Loans</h1>
            <div className={classes.loans}>
                <Loan amount='1234' />
            </div>
            </section>
        );
    }
};

export default AllLoans;
